import{_ as s,C as r,c as n,o as d,aA as t,j as i,G as a,a as l,w as p}from"./chunks/framework.DpoPorFA.js";const g="/RegionGrids.jl/dev/assets/typesofgrids.pXI8p_IV.png",m=JSON.parse('{"title":"What is a RegionGrid?","description":"","frontmatter":{},"headers":[],"relativePath":"basics/regiongrids.md","filePath":"basics/regiongrids.md","lastUpdated":null}'),c={name:"basics/regiongrids.md"},h={class:"jldocstring custom-block",open:""};function u(_,e,f,G,R,T){const o=r("Badge");return d(),n("div",null,[e[4]||(e[4]=t('<h1 id="What-is-a-RegionGrid?" tabindex="-1">What is a RegionGrid? <a class="header-anchor" href="#What-is-a-RegionGrid?" aria-label="Permalink to &quot;What is a RegionGrid? {#What-is-a-RegionGrid?}&quot;">​</a></h1><p>A <code>RegionGrid</code> contains information that:</p><ul><li><p>Allows us to extract gridded lon-lat data for a given <code>GeoRegion</code> (see <a href="https://github.com/GeoRegionsEcosystem/GeoRegions.jl" target="_blank" rel="noreferrer">GeoRegions.jl</a>) of interest.</p></li><li><p>Subset the relevant longitude/latitude vectors from the initial grid.</p></li><li><p>Allows for easy spatial-averaging of extracted gridded lon-lat data, weighted by latitude.</p></li></ul>',3)),i("details",h,[i("summary",null,[e[0]||(e[0]=i("a",{id:"RegionGrids.RegionGrid",href:"#RegionGrids.RegionGrid"},[i("span",{class:"jlbinding"},"RegionGrids.RegionGrid")],-1)),e[1]||(e[1]=l()),a(o,{type:"info",class:"jlObjectType jlType",text:"Type"})]),e[3]||(e[3]=t('<div class="language-julia vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">julia</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">RegionGrid</span></span></code></pre></div><p>Abstract supertype for geographical region gridded information.</p>',2)),a(o,{type:"info",class:"source-link",text:"source"},{default:p(()=>e[2]||(e[2]=[i("a",{href:"https://github.com/GeoRegionsEcosystem/RegionGrids.jl/blob/62577d8968982925d336b4df23b35a6647a8e2c3/src/RegionGrids.jl#L17-L21",target:"_blank",rel:"noreferrer"},"source",-1)])),_:1})]),e[5]||(e[5]=t('<h2 id="The-Types-of-RegionGrids" tabindex="-1">The Types of <code>RegionGrid</code>s <a class="header-anchor" href="#The-Types-of-RegionGrids" aria-label="Permalink to &quot;The Types of `RegionGrid`s {#The-Types-of-RegionGrids}&quot;">​</a></h2><p>The <code>RegionGrid</code> abstract type has three subtypes:</p><ol><li><p><code>RectilinearGrid</code> type, which is for the extraction of data on rectilinear lon-lat grids</p></li><li><p><code>GeneralizedGrid</code> type, which is for the extraction of data on non-rectilinear lon-lat grids, such as a curvilinear grid.</p></li><li><p><code>UnstructuredGrid</code> type, which is for the extraction of data on unstructured lon-lat grids such as a cubed-spherical grid, or an unstructured mesh.</p></li></ol><p><img src="'+g+'" alt=""></p><p>Confusing? Fret not, it&#39;s always easier to understand by doing, so let&#39;s go through an example <a href="/RegionGrids.jl/dev/basics/example">here</a>.</p>',5))])}const y=s(c,[["render",u]]);export{m as __pageData,y as default};
